mapper.select:
SELECT ar.*, hh.short_link, hc2.content as content, hc1.content as reContext, ui.user_name as reAuthorName, uz.user_name as authorName, hc1.comment_date as commentDate, hc1.re_id from hts_admin.interact_auto_response as ar left join hts_admin.interact_user_level_list iull on iull.user_id=ar.re_author, hts.user_info as uz, hts.user_info as ui, hts.htworld_comment as hc1, hts.htworld_comment as hc2, hts.htworld_htworld as hh where hc1.id = ar.comment_id and hc2.id = ar.response_id and uz.id = ar.author and ui.id = ar.re_author and ar.complete = 0 and hh.id = ar.world_id and hh.valid = 1
and iull.user_level_id = #{userLevelId}
and ar.id<=#{maxId}
ORDER BY ar.id desc

select count(1) from ( select ar.* from hts_admin.interact_auto_response as ar left join hts_admin.interact_user_level_list iull on iull.user_id=ar.re_author where ar.complete=0
and iull.user_level_id = #{userLevelId}
and ar.id<=#{maxId}
) as ar1 left join hts.htworld_htworld hh on ar1.world_id=hh.id where hh.valid=1

select ar.*
from hts_admin.interact_auto_response ar
where ar.id=#{id}
