mapper.update:
update hts.operations_channel_count_base

	world_count = #{worldBaseCount,jdbcType=INTEGER},
	child_count = #{childBaseCount,jdbcType=INTEGER},
	member_count = #{memberBaseCount,jdbcType=INTEGER},
	superb_count = #{superbBaseCount,jdbcType=VARCHAR}

where channel_id = #{channelId,jdbcType=INTEGER}

mapper.select:
select count(*) from hts.operations_channel_count_base cb, hts_admin.admin_user_relationship_binding au, hts.operations_channel c
AND cb.channel_id<=#{maxId} c.owner_id = au.user_id and c.id = cb.channel_id
and au.admin_user_id = #{adminId}
