mapper.update:
update 
	hts.operations_channel_member

	
		degree = #{degree},
	
	
		shield = #{shield},
	
	
		channel_star = #{channelStar}
	
 

	
		id = #{channelMemberId}
	
	
		and user_id = #{userId} and channel_id = #{channelId}

mapper.select:
select cms.id as channel_member_id, cms.channel_id, cms.user_id, cms.sub_time, cms.degree, cms.shield, cms.channel_star, cms.notified, u.user_name, u.user_avatar, u.sex, u.user_label, u.concern_count, u.follow_count, u.world_count, u.phone_code, u.star from (select ocm.id, ocm.channel_id, ocm.user_id, ocm.sub_time, ocm.degree, ocm.shield, ocm.channel_star, ocs.notified, ocs.serial from hts.operations_channel_member ocm left join hts.operations_channel_star ocs on ocm.id = ocs.id
and ocm.channel_id = #{channelId}
and ocm.user_id = #{userId}
and ocm.shield = #{shield}
and ocs.notified = #{notified}
and ocm.id <= #{maxId}
order by ocs.serial desc, ocm.id desc
) as cms, hts.user_info u
u.star = #{star}
and u.user_name like CONCAT('%',#{userName},'%')
and cms.user_id = u.id

select 
	count(*) 
from 
	hts.operations_channel_member ocm,
	hts.user_info u

	
		ocm.channel_id = #{channelId}
	
	
		and ocm.user_id = #{userId}
	
	
		and ocm.shield = #{shield}
	
	
		and u.user_name like CONCAT('%',#{userName},'%')
	
	
		 and ocm.id <= #{maxId} 
	
	and ocm.user_id = u.id

mapper.update:
update hts.operations_channel_star

	
		notified = #{notified}
	
	
		serial = #{serial}
	

where id = #{channelMemberId}
