mapper.update:
update hts_admin.op_channel_world_superb_schedula


		date_schedula = #{schedulaDate,jdbcType=TIMESTAMP},


		operator_id = #{operatorId},


		finish = #{finish},

	date_modify = #{modifyDate,jdbcType=TIMESTAMP}

where

		id = #{id}



			and world_id = #{worldId}
			and channel_id = #{channelId}

mapper.select:
select ocwss.*,aui.user_name as operator_name,hh.short_link,hh.author_id as user_id,oc.channel_name,ui.user_name from
hts_admin.op_channel_world_superb_schedula ocwss

left join 
hts_admin.admin_user_info aui
on ocwss.operator_id = aui.id
left join
hts.operations_channel oc
on ocwss.channel_id=oc.id
left join 
hts.htworld_htworld hh
on ocwss.world_id = hh.id
left join
hts.user_info ui
on hh.author_id = ui.id


		ocwss.id = #{id}


		ocwss.world_id = #{worldId}


		and ocwss.channel_id = #{channelId}


		and ocwss.finish = #{finish}


		 and ocwss.date_schedula >= #{addDate,jdbcType=TIMESTAMP} 


		 and ocwss.date_schedula <= #{modifyDate,jdbcType=TIMESTAMP} 


		 and ocwss.id <= #{maxId}


order by ocwss.id desc

select count(ocwss.id)
from hts_admin.op_channel_world_superb_schedula ocwss


		ocwss.id = #{id}


		ocwss.world_id = #{worldId}


		and ocwss.channel_id = #{channelId}


		and ocwss.finish = #{finish}


		 and ocwss.date_schedula >= #{addDate,jdbcType=TIMESTAMP} 


		 and ocwss.date_schedula <= #{modifyDate,jdbcType=TIMESTAMP} 


		 and ocwss.id <= #{maxId}
